
<a {% if action is defined and action | length %}href="{{ action }}"{% endif %} class="btn{% if type is defined and type != 'primary' %} btn--{{type}}{% endif %}{% if small is defined and small == 'true' %} btn--small{% endif %}{% if active is defined and active == 'true' %} active{% endif %}{% if iconPosition is defined %} btn--icon-{{iconPosition}}{% endif %}" role="button">{% if icon is defined and iconPosition is defined and iconPosition == 'before' %}<i class="{{icon}}" aria-hidden></i> {% endif %}{% if text is defined and text | length %}{{text}}{% endif %}{% if icon is defined and iconPosition is defined and iconPosition == 'after' %} <i class="{{icon}}" aria-hidden></i>{% endif %}</a>